package com.tydic.scaffold.common.controller;

import com.tydic.core.rational.Return;
import com.tydic.core.rational.ReturnCode;
import com.tydic.core.util.json.JSON;
import com.tydic.framework.oauth2.helper.AbstractOauth2HelperBean;
import com.tydic.framework.spring.security.SecurityConstants;
import com.tydic.scaffold.extension.JwtTokenSetterBaseTest;
import org.apache.commons.io.FileUtils;
import org.junit.Assert;
import org.junit.jupiter.api.BeforeAll;
import org.junit.jupiter.api.Disabled;
import org.junit.jupiter.api.Test;
import org.junit.jupiter.api.TestInstance;
import org.junit.jupiter.api.extension.ExtendWith;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.boot.test.autoconfigure.web.servlet.AutoConfigureMockMvc;
import org.springframework.boot.test.context.SpringBootTest;
import org.springframework.http.MediaType;
import org.springframework.mock.web.MockMultipartFile;
import org.springframework.test.context.junit.jupiter.SpringExtension;
import org.springframework.test.web.servlet.MockMvc;
import org.springframework.test.web.servlet.MvcResult;
import org.springframework.test.web.servlet.ResultHandler;
import org.springframework.test.web.servlet.request.MockMvcRequestBuilders;

import java.io.File;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.Paths;

import static org.springframework.test.web.servlet.result.MockMvcResultMatchers.status;

@ExtendWith(SpringExtension.class)
@SpringBootTest
@AutoConfigureMockMvc
@Disabled
@TestInstance(TestInstance.Lifecycle.PER_CLASS)
public class AttachmentControllerTest extends JwtTokenSetterBaseTest {

    @Autowired
    private MockMvc mockMvc;

    @Test
    public void uploadAttachment() throws Exception {
        Path path = Paths.get("c:/a.txt");

        /**
         * name : request接收请求的参数名
         * originalFilename： 附件名称
         * MockMultipartFile(String name, @Nullable String originalFilename, @Nullable String contentType, @Nullable byte[] content)
         */
        MockMultipartFile firstFile = new MockMultipartFile("file", "a.txt", "text/plain", Files.readAllBytes(path));

        mockMvc.perform(MockMvcRequestBuilders.multipart("/common/attachment/upload")
                .file(firstFile)
                .header(SecurityConstants.JWT_HEADER_AUTHORIZATION_CODE, jwtToken)
                .accept(MediaType.APPLICATION_JSON)
        )
                .andExpect(status().isOk())
                .andDo(new ReturnJsonResultHandler());
    }

    @Test
    public void base64Upload() throws Exception {
        String base64Str = "data:image/png;base64,";

        mockMvc.perform(MockMvcRequestBuilders.post("/common/attachment/base64Upload")
                .param("base64", base64Str)
                .param("fileName", "测试.png")
                .header(SecurityConstants.JWT_HEADER_AUTHORIZATION_CODE, jwtToken)
                .accept(MediaType.APPLICATION_OCTET_STREAM)
        )
                .andExpect(status().isOk())
                .andDo(new ReturnJsonResultHandler());
    }

    @Test
    public void downLoad() throws Exception {
        mockMvc.perform(MockMvcRequestBuilders.get("/common/attachment/download/123")
                .header(SecurityConstants.JWT_HEADER_AUTHORIZATION_CODE, jwtToken)
        )
                .andExpect(status().isOk())
                .andDo(result -> {
                    //下载文件： 存放到c:/temp_1.jpg
                    FileUtils.writeByteArrayToFile(new File("c:/temp_1.JPG"), result.getResponse().getContentAsByteArray());
                });
    }

    /**
     * 下载附件：jwtToken 拼接在url后,
     *
     * @throws Exception
     */
    @Test
    public void downLoad2() throws Exception {
        mockMvc.perform(MockMvcRequestBuilders.get("/common/attachment/download/123")
                .param(SecurityConstants.URL_JWT_PARAMETER_AUTHORIZATION_CODE, jwtToken)
        )
                .andExpect(status().isOk())
                .andDo(result -> {
                    //下载文件： 存放到c:/temp_1.jpg
                    FileUtils.writeByteArrayToFile(new File("c:/temp_1.JPG"), result.getResponse().getContentAsByteArray());
                });
    }


    class ReturnJsonResultHandler implements ResultHandler {

        @Override
        public void handle(MvcResult result) throws Exception {
            String json = result.getResponse().getContentAsString();

            System.out.println("base64获取响应信息为：\n" + json);
            Return actual = JSON.parse(json, Return.class);
            Assert.assertEquals(actual.getCode(), ReturnCode.SUCCESS);
        }
    }


}